import {useState} from 'react'
import axios from 'axios'
import {Data} from './components/SponsorsDepartment/SponsorsForm/Data'

function App() {

 // form states
 const [name, setName]=useState('');
 const [age, setAge]=useState('');

 // retrived data state
 const [data, setData]=useState([]);

 // submit event
 const handleSubmit=(e)=>{
   e.preventDefault();
   // console.log(name, age, designation, salary);

   // our object to pass
   const data = {
     name,age,
   }
   axios.post('https://sheet.best/api/sheets/a99d10a3-c35e-4756-80e4-3c8eaa18b229',data).then(response=>{
     // console.log(response);
     setName('');
     setAge('');
   })
 }

 // getting data function
 const getData=()=>{
   axios.get('https://sheet.best/api/sheets/a99d10a3-c35e-4756-80e4-3c8eaa18b229').then((response)=>{
     setData(response.data);
   })
 }

 // triggering function
 useEffect(()=>{
   getData();
 },[data])

return (
  <div className="container">
    <br></br>
    <h1>Test Drive</h1>
    <br></br>
    <form autoComplete="off" className='form-group'
    onSubmit={handleSubmit}>
      <label>Name</label>
      <input type='text' className='form-control' required
        placeholder='Enter your name' onChange={(e)=>setName(e.target.value)}
        value={name}
      />
      <br></br>
      <label>Age</label>
      <input type='text' className='form-control' required
        placeholder='Enter your age' onChange={(e)=>setAge(e.target.value)}
        value={age}
      />
      <br></br>
      <div style={{display:"flex",justifyContent:'flex-end'}}>
        <button type='submit' className='btn btn-primary'>Submit</button>
      </div>
    </form>
    <div className='view-data'>
      {data.length<1&&<>No data to show</>}
      {data.length>0&&(
        <div className='table-responsive'>
          <table className='table'>
            <thead>
              <tr>
                <th scope='col'>Index</th>
                <th scope='col'>Name</th>
                <th scope='col'>Age</th>
              </tr>
            </thead>
            <tbody>
              <Data data={data}/>
            </tbody>
          </table>
        </div>
      )}
    </div>
  </div>
);
      }
