import React from "react";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import Container from "react-bootstrap/Container";

import logo from "../images/navbar/logo.png";
import usFlag from "../images/navbar/us-flag.svg";
import bgFlag from "../images/navbar/bg-flag.svg";

import { Link, animateScroll as scroll } from "react-scroll";

import { Link as RouterLink } from "react-router-dom";

import "./styles/NavBar.css";

const navItems = [
	{
		to: "about",
		text: {
			en: "What We Do",
			bg: "Кои Сме Ние",
		},
	},
	{
		to: "events",
		text: {
			en: "How We Do It",
			bg: "Какво Правим",
		},
	},
	{
		to: "speakers",
		text: {
			en: "Our Guests",
			bg: "Наши Гости",
		},
	},
	{
		to: "sponsors",
		text: {
			en: "Our Partners",
			bg: "Наши Партньори",
		},
	},
	{
		to: "team",
		text: {
			en: "Our Team",
			bg: "Екипът",
		},
	},
	{
		to: "contact",
		text: {
			en: "Contact",
			bg: "Контакт",
		},
	},
];

export default function NavBar(props) {
	const switchLang = (lang) => {
		if (lang === "en") return "/bg";
		if (lang === "bg") return "/en";
		return "/en";
	};

	return (
		<Navbar collapseOnSelect expand="xl" bg="light" fixed="top">
			<Container fluid>
				<Navbar.Brand onClick={() => scroll.scrollToTop()}>
					<img
						src={logo}
						width="30"
						height="30"
						className="d-inline-block align-top"
						alt=""
						style={{ marginRight: "0.2em" }}
					/>
					STARTUP@BLAGOEVGRAD
				</Navbar.Brand>
				<Navbar.Toggle aria-controls="responsive-navbar-nav" />
				<Navbar.Collapse id="responsive-navbar-nav">
					<Nav className="ml-auto">
						{/*<div className='nav-link'>
            <RouterLink to='/apply' className={`apply-button nav-link-${props.lang}`} >APPLY NOW</RouterLink>
          </div> */}

						<div className="nav-link">
							<RouterLink
								to="/Weekend2022"
								className="apply-button nav-link-en"
							>
								Weekend2022
							</RouterLink>
						</div>

						{navItems.map((item, index) => (
							<Nav.Link
								as={Link}
								key={index}
								activeClass="active"
								to={item.to}
								spy={true}
								smooth={true}
								offset={-60}
								duration={500}
								className={"nav-link-" + props.lang}
							>
								<span>{item.text[props.lang]}</span>
							</Nav.Link>
						))}

						<div className="nav-link">
							<RouterLink
								to={switchLang(props.lang)}
								className="lang-button"
							>
								{props.lang === "en" && (
									<img
										onClick={() =>
											localStorage.setItem("lang", "bg")
										}
										className="bg-flag"
										src={bgFlag}
										style={{
											marginTop: "-0.25em",
											height: "1.1em",
										}}
										alt="BG"
									></img>
								)}
								{props.lang === "bg" && (
									<img
										src={usFlag}
										onClick={() =>
											localStorage.setItem("lang", "en")
										}
										style={{
											marginTop: "-0.25em",
											height: "1.1em",
										}}
										alt="EN"
									></img>
								)}
							</RouterLink>
						</div>
					</Nav>
				</Navbar.Collapse>
			</Container>
		</Navbar>
	);
}
