import React from "react";
import "./apply.css";
import "./MarketingDep.css";
import MarketingDep from "./MarketingDep";
import MarketingEvent from "./MarketingEvent";

const eventsText = {

    "weekendText": {
        "en": "The second big event that the club organizes is the StartUP Weekend. It is a 3-day-long event, taking place from Friday until Sunday, in which the participants are posed with the challenge to come up with an idea or a start-up. The participants, divided into teams, have to come up with an original business idea, polish it out and present it in front of a professional jury, who then decide on which 3 ideas and executions are the best. Throughout the duration of the weekend, the teams have mentors, who are there to help them and ensure that their idea is more than ready to be seen by the judges.",
    
    }
}

export default function MarketingForm(lang) {

    return (
        <div className="marketing-form">
            <MarketingEvent lang={lang} />
            <div className="container text-center">
                <h5 className="title py-4">Marketing Department</h5>
                <p className="scroll-down">Scroll Down To Apply</p>

                <div className="row">
                    <div className="col-xl mt-3">
                    <div>
                   
                        <div className="desc-text mt-3">
                            Painters, writers, photographers, the Marketing
                            department houses all the creative souls within
                            itself. Creating and executing different campaigns
                            for events, creating posters, stickers, editing
                            photos and videos, the people in the marketing
                            department try their best to provide a splash of
                            color in people's otherwise gray everyday lives. If
                            you think you can fit into this colorful crayon box,
                            if you want to be the pop of blue in the sea of
                            gray, the Marketing department will welcome you with
                            open arms.
                         </div>
                    </div>
                    </div>
                </div>
            </div>
            <MarketingDep lang={lang} />
        </div>
    );
}
